      };
      console.log('‚úì Binarios recuperados de Procesar FormData:', Object.keys(binaryData || {}));
    }
  } catch (e) {
    console.log('Error al acceder a Procesar FormData:', e.message);
  }
}

// Verificar si ahora tenemos binarios
if (!binaryData || Object.keys(binaryData).length === 0) {
  throw new Error('No se encontraron datos binarios. Verifica que las im√°genes se hayan cargado correctamente en el webhook.');
}

console.log('Binarios disponibles:', Object.keys(binaryData));

// L√ìGICA DE SELECCI√ìN - Buscar imagen que empiece con "Email"
let selectedBinaryKey = null;
let selectedMetadata = null;

// Buscar por nombre "Email"
for (const [key, file] of Object.entries(binaryData)) {
  if (file.fileName && file.fileName.toLowerCase().startsWith('email')) {
    selectedBinaryKey = key;
    selectedMetadata = {
      fileName: file.fileName,
      mimeType: file.mimeType,
      originalKey: key
    };
    console.log(`‚úì ENCONTRADA imagen "Email": ${file.fileName}`);
    break;
  }
}

// Si no hay imagen "Email", usar la primera
if (!selectedBinaryKey) {
  selectedBinaryKey = Object.keys(binaryData)[0];
  const file = binaryData[selectedBinaryKey];
  selectedMetadata = {
    fileName: file.fileName,
    mimeType: file.mimeType,
    originalKey: selectedBinaryKey
  };
  console.log(`‚ö†Ô∏è No se encontr√≥ imagen "Email", usando: ${file.fileName}`);
}

// Generar nombre limpio y rutas
const cleanFileName = `infografia_${selectedMetadata.fileName.replace(/[^a-zA-Z0-9\.]/g, '_')}`;
const projectId = projectData.project_id || `proyecto_${Date.now()}`;
const infografiaFolder = `/Documentos compartidos/HTML_Mailings/${projectId}/infografia`;
const targetPath = `${infografiaFolder}/${cleanFileName}`;

console.log(`‚úì Infograf√≠a seleccionada: ${selectedMetadata.fileName}`);
console.log(`‚úì Se guardar√° como: ${cleanFileName}`);

return [{
  json: {
    ...projectData,
    project_id: projectId,
    infografia_uploaded: {
      ...selectedMetadata,
      cleanFileName: cleanFileName,
      targetPath: targetPath,
      relativePath: `./infografia/${cleanFileName}`,
      selectionMethod: selectedMetadata.fileName.toLowerCase().startsWith('email') ? 'by_email_name' : 'fallback_first_image'
    },
    INFOGRAFIA_FOLDER: infografiaFolder
  },
  binary: {
    data: binaryData[selectedBinaryKey]
  }
}];

// NODO: Preparar Elemento - VERSI√ìN CORREGIDA
// Este c√≥digo prepara TODOS los elementos EXCEPTO la infograf√≠a (im√°genes que empiezan con "Email")

const inputItems = $input.all();

console.log('=== DEBUG PREPARAR ELEMENTOS (NO INFOGRAF√çA) ===');
console.log('Input actual tiene binary?', !!inputItems[0].binary);

// Obtener los datos binarios y del proyecto
let binaryData = inputItems[0].binary;
let projectData = inputItems[0].json;

// Si no hay binarios en el input actual, buscarlos en el nodo Procesar FormData
if (!binaryData || Object.keys(binaryData).length === 0) {
  console.log('No hay binarios en input actual, buscando en Procesar FormData...');

  try {
    const procesarFormDataItems = $('Procesar FormData1').all();
    if (procesarFormDataItems && procesarFormDataItems[0]) {
      binaryData = procesarFormDataItems[0].binary;
      const imageMetadata = procesarFormDataItems[0].json;
      projectData = {
        ...projectData,
        ...imageMetadata
      };
      console.log('‚úì Binarios recuperados de Procesar FormData:', Object.keys(binaryData || {}));
    }
  } catch (e) {
    console.log('Error al acceder a Procesar FormData:', e.message);
  }
}

// Verificar que tenemos binarios
if (!binaryData || Object.keys(binaryData).length === 0) {
  console.log('‚ö†Ô∏è No se encontraron datos binarios');
  return [{
    json: {
      ...projectData,
      no_elements: true,
      elements_count: 0,
      message: 'No hay elementos adicionales para procesar'
    }
  }];
}

console.log('Binarios disponibles:', Object.keys(binaryData));

// L√ìGICA DE SELECCI√ìN - Filtrar todas las im√°genes EXCEPTO las que empiecen con "Email"
const elementosParaProcesar = [];

for (const [key, file] of Object.entries(binaryData)) {
  // EXCLUIR im√°genes que empiecen con "Email" (son infograf√≠as)
  if (file.fileName && !file.fileName.toLowerCase().startsWith('email')) {
    elementosParaProcesar.push({
      binaryKey: key,
      fileName: file.fileName,
      mimeType: file.mimeType || 'image/jpeg',
      originalName: file.fileName,
      binaryData: file
    });
    console.log(`‚úì Elemento a√±adido: ${file.fileName}`);
  } else {
    console.log(`‚ö†Ô∏è Excluido (es infograf√≠a): ${file.fileName}`);
  }
}

// Si no hay elementos adicionales para procesar
if (elementosParaProcesar.length === 0) {
  console.log('‚ÑπÔ∏è No hay elementos adicionales (solo infograf√≠a)');
  return [{
    json: {
      ...projectData,
      no_elements: true,
      elements_count: 0,
      message: 'Solo se encontr√≥ la infograf√≠a principal, no hay elementos adicionales'
    }
  }];
}

console.log(`üìÑ Total elementos a procesar: ${elementosParaProcesar.length}`);

// Configurar carpeta de destino
const projectId = projectData.project_id || `proyecto_${Date.now()}`;
const elementosFolder = projectData.ELEMENTOS_FOLDER || `/Documentos compartidos/HTML_Mailings/${projectId}/elementos`;

// Preparar array de resultados - UN ITEM POR CADA ELEMENTO
const results = [];
const allProcessedElements = [];

for (let i = 0; i < elementosParaProcesar.length; i++) {
  const elemento = elementosParaProcesar[i];

  // Generar nombre limpio para el elemento
  const cleanFileName = `elemento_${String(i + 1).padStart(2, '0')}_${elemento.fileName.replace(/[^a-zA-Z0-9\.]/g, '_')}`;
  const targetPath = `${elementosFolder}/${cleanFileName}`;
  const relativePath = `./elementos/${cleanFileName}`;

  // Crear metadata del elemento procesado
  const processedElement = {
    fileName: elemento.fileName,
    cleanFileName: cleanFileName,
    targetPath: targetPath,
    relativePath: relativePath,
    mimeType: elemento.mimeType,
    originalKey: elemento.binaryKey,
    uploadIndex: i + 1,
    totalElements: elementosParaProcesar.length
  };

  allProcessedElements.push(processedElement);

  console.log(`‚úì Preparado elemento ${i + 1}/${elementosParaProcesar.length}: ${cleanFileName}`);

  // Crear resultado individual con el binario correspondiente
  results.push({
    json: {
      ...projectData,
      element_uploaded: processedElement,
      current_element: processedElement,
      element_index: i,
      total_elements: elementosParaProcesar.length,
      is_last: i === elementosParaProcesar.length - 1,
      all_processed_elements: allProcessedElements,
      ELEMENTOS_FOLDER: elementosFolder
    },
    binary: {
      data: elemento.binaryData
    }
  });
}

console.log('=== RESUMEN PROCESAMIENTO ELEMENTOS ===');
console.log(`‚úÖ Total elementos preparados: ${results.length}`);
console.log(`üìÅ Carpeta destino: ${elementosFolder}`);

// Retornar todos los elementos preparados
return results;

// NODO: Inyectar URLs SharePoint
// Colocar DESPU√âS de que todas las im√°genes se hayan subido
let htmlContent;
try {
  htmlContent = $('Procesar HTML con placeholder').first().json.html_con_placeholders;
} catch (e) {
  throw new Error("Nodo 'Procesar HTML con placeholder' no existe o no tiene salida.");
}

// Obtener URLs reales de SharePoint
const urlMapping = {};

// Desde Subir Infograf√≠a
try {
  const infografia = $('Subir Infograf√≠a').first().json;
  if (infografia && infografia.webUrl) {
    // La infograf√≠a principal normalmente ser√≠a el header
    urlMapping['{{CABECERA_URL}}'] = infografia.webUrl;
    console.log('‚úì Infograf√≠a mapeada como cabecera');
  }
} catch (e) {
  console.log('No se pudo obtener infograf√≠a');
}

// Desde Subir Elemento - MAPEO INTELIGENTE
try {
  const elementos = $('Subir Elemento').all();
  if (!elementos || !Array.isArray(elementos)) {
    throw new Error("'Subir Elemento' no tiene elementos de salida.");
  }

  // Mapeo por nombre de archivo PRIMERO
  elementos.forEach(item => {
    if (item.json && item.json.name) {
      const fileName = item.json.name.toLowerCase();
      const url = item.json.webUrl;

      // Mapeo basado en nombres
      if (fileName.includes('banner') && !urlMapping['{{BANNER_URL}}']) {
        urlMapping['{{BANNER_URL}}'] = url;
      } else if (fileName.includes('cabecera') && !urlMapping['{{CABECERA_URL}}']) {
        urlMapping['{{CABECERA_URL}}'] = url;
      } else if (fileName.includes('credicorp') && !urlMapping['{{CREDICORP_URL}}']) {
        urlMapping['{{CREDICORP_URL}}'] = url;
      } else if (fileName.includes('cuadro') && !urlMapping['{{CUADRO_URL}}']) {
        urlMapping['{{CUADRO_URL}}'] = url;
      } else if (fileName.includes('icon1') && !urlMapping['{{ICON1_URL}}']) {
        urlMapping['{{ICON1_URL}}'] = url;
      } else if (fileName.includes('icon2') && !urlMapping['{{ICON2_URL}}']) {
        urlMapping['{{ICON2_URL}}'] = url;
      } else if (fileName.includes('icon3') && !urlMapping['{{ICON3_URL}}']) {
        urlMapping['{{ICON3_URL}}'] = url;
      } else if (fileName.includes('facebook') && !urlMapping['{{FACEBOOK_URL}}']) {
        urlMapping['{{FACEBOOK_URL}}'] = url;
      } else if (fileName.includes('instagram') && !urlMapping['{{INSTAGRAM_URL}}']) {
        urlMapping['{{INSTAGRAM_URL}}'] = url;
      } else if (fileName.includes('linkedin') && !urlMapping['{{LINKEDIN_URL}}']) {
        urlMapping['{{LINKEDIN_URL}}'] = url;
      } else if (fileName.includes('youtube') && !urlMapping['{{YOUTUBE_URL}}']) {
        urlMapping['{{YOUTUBE_URL}}'] = url;
      }
    }
  });

  // Mapeo por posici√≥n SI faltan elementos cr√≠ticos
  if (!urlMapping['{{CABECERA_URL}}'] && elementos.length > 0) {
    urlMapping['{{CABECERA_URL}}'] = elementos[0].json.webUrl;
    console.log('‚úì Primera imagen asignada como cabecera');
  }

  if (!urlMapping['{{CREDICORP_URL}}'] && elementos.length > 0) {
    // Buscar imagen con "credicorp" o usar la √∫ltima
    const credicorpImg = elementos.find(item => 
      item.json.name && item.json.name.toLowerCase().includes('credicorp')
    );
    urlMapping['{{CREDICORP_URL}}'] = credicorpImg 
      ? credicorpImg.json.webUrl 
      : elementos[elementos.length - 1].json.webUrl;
  }

  // Asignar iconos gen√©ricos a im√°genes no mapeadas
  let iconCounter = 1;
  elementos.forEach(item => {
    const url = item.json.webUrl;
    // Si esta URL no est√° ya mapeada
    if (!Object.values(urlMapping).includes(url) && iconCounter <= 3) {
      urlMapping[`{{ICON${iconCounter}_URL}}`] = url;
      iconCounter++;
    }
  });

} catch (e) {
  console.log('Error mapeando elementos:', e.message);
}

// URLs por defecto para redes sociales FUERA del bucle
const defaultSocialUrls = {
  '{{FACEBOOK_URL}}': 'https://www.facebook.com/PrimaAFP',
  '{{INSTAGRAM_URL}}': 'https://www.instagram.com/primaafp/',
  '{{LINKEDIN_URL}}': 'https://pe.linkedin.com/company/prima-afp',
  '{{YOUTUBE_URL}}': 'https://www.youtube.com/user/AFPPrima'
};

// Aplicar URLs por defecto solo si no existen
Object.keys(defaultSocialUrls).forEach(key => {
  if (!urlMapping[key]) {
    urlMapping[key] = defaultSocialUrls[key];
  }
});

// URL por defecto para cuadro decorativo
if (!urlMapping['{{CUADRO_URL}}']) {
  urlMapping['{{CUADRO_URL}}'] = '#'; // O una imagen placeholder
}

// Reemplazar placeholders con URLs reales
Object.keys(urlMapping).forEach(placeholder => {
  const url = urlMapping[placeholder];
  htmlContent = htmlContent.replace(new RegExp(placeholder.replace(/[{}]/g, '\\$&'), 'g'), url);
  console.log(`‚úì Reemplazado: ${placeholder}`);
});

// Verificar placeholders no reemplazados
const remaining = htmlContent.match(/\{\{[A-Z_]+_URL\}\}/g) || [];
if (remaining.length > 0) {
  console.log(`‚ö†Ô∏è Placeholders sin reemplazar: ${remaining.join(', ')}`);
  remaining.forEach(placeholder => {
    htmlContent = htmlContent.replace(new RegExp(placeholder.replace(/[{}]/g, '\\$&'), 'g'), '#');
  });
}

// Preparar respuesta final
let projectId, projectName;
try {
  const formData = $('Procesar FormData').first().json;
  projectId = formData.project_id;
  projectName = formData.project_name;
} catch(e) {
  projectId = '';
  projectName = 'proyecto_desconocido';
}
const timestamp = new Date().toISOString();

// HTML final con comentarios
const finalHtml = `<!-- 
  Generado: ${timestamp}
  Proyecto: ${projectName}
  ID: ${projectId}
  URLs mapeadas: ${Object.keys(urlMapping).length}
-->
${htmlContent}`;

return {
  json: {
    generated_html: finalHtml,
    html_file_name: `${projectName}_email.html`,
    urls_mapeadas: urlMapping,
    urls_reemplazadas: Object.keys(urlMapping).length,
    placeholders_restantes: remaining.length,
    ready_to_upload: true
  }
};

// NODO: Procesar HTML - VERSI√ìN CORREGIDA PARA ELIMINAR NARRATIVA
const response = $input.first().json;
const projectData = response;

// Extraer HTML de Claude
let htmlContent = '';
if (response.content && Array.isArray(response.content)) {
  htmlContent = response.content[0].text || response.content[0];
}

// ELIMINAR TODA LA NARRATIVA DE CLAUDE
// 1. Encontrar donde empieza el HTML real
const htmlStart = htmlContent.indexOf('<!DOCTYPE');
const htmlEnd = htmlContent.lastIndexOf('</html>') + 7;

if (htmlStart !== -1 && htmlEnd > htmlStart) {
  // Extraer SOLO el HTML
  htmlContent = htmlContent.substring(htmlStart, htmlEnd);
} else {
  // Si no encuentra DOCTYPE, intentar con <html
  const altStart = htmlContent.indexOf('<html');
  if (altStart !== -1) {
    htmlContent = htmlContent.substring(altStart, htmlEnd);
  }
}

// Limpiar cualquier marcador de c√≥digo markdown residual
htmlContent = htmlContent
  .replace(/```html\n?/gi, '')
  .replace(/```\n?$/gi, '')
  .replace(/^```\n?/gi, '')
  .trim();

// VALIDACI√ìN DE ESTRUCTURA PRIMA AFP
const validationErrors = [];

if (!htmlContent.includes('width="600"')) {
  validationErrors.push('Falta tabla principal de 600px');
}
if (!htmlContent.includes('#FF4F00')) {
  validationErrors.push('Falta color naranja corporativo');
}
if (!htmlContent.includes('PRIMA AFP S.A. RUC')) {
  validationErrors.push('Falta footer legal');
}
if (!htmlContent.includes('[Nombre del Cliente]')) {
  validationErrors.push('Falta placeholder de personalizaci√≥n');
}

// Log de advertencias
if (validationErrors.length > 0) {
  console.log('‚ö†Ô∏è Advertencias de validaci√≥n:', validationErrors);
}

// Validar placeholders
const placeholders = htmlContent.match(/\{\{[A-Z_]+_URL\}\}/g) || [];
console.log(`Placeholders encontrados: ${placeholders.length}`);

// Verificar que es HTML v√°lido
const isValidHTML = htmlContent.startsWith('<!DOCTYPE') || htmlContent.startsWith('<html');
if (!isValidHTML) {
  console.log('‚ö†Ô∏è ADVERTENCIA: El contenido no parece ser HTML v√°lido');
}

// RETURN con HTML limpio
return {
  json: {
    ...projectData,
    html_con_placeholders: htmlContent,
    placeholders_encontrados: placeholders,
    validation_errors: validationErrors,
    validation_passed: validationErrors.length === 0,
    necesita_reemplazo: true,
    is_valid_html: isValidHTML
  }
};

// Procesar FormData con manejo correcto de filesystem-v2
const items = $input.all();

console.log('=== DEBUG PROCESAR FORMDATA ===');
console.log('N√∫mero de items recibidos:', items.length);

if (!items || items.length === 0) {
  throw new Error('No se recibieron datos en el webhook');
}

const inputData = items[0];
console.log('Tiene datos binarios:', !!inputData.binary);
console.log('Claves binarias:', inputData.binary ? Object.keys(inputData.binary) : 'ninguna');

// Variables para datos procesados
let formData = {};
let files = {};
let projectName = null;

// Procesar datos del body
if (inputData.json) {
  formData = inputData.json;
  console.log('‚úì Usando json para formData');
}

// IMPORTANTE: Los archivos binarios est√°n directamente en inputData.binary
if (inputData.binary) {
  files = inputData.binary;
  console.log('‚úì Archivos binarios encontrados:', Object.keys(files));
}

// Buscar projectName
projectName = formData.projectName || 
              inputData.json?.projectName ||
              inputData.query?.projectName ||
              `proyecto_${Date.now()}`;

console.log(`‚úì Proyecto: ${projectName}`);

// Procesar todas las im√°genes
const allImages = [];
let imageCount = 0;

// Procesar cada archivo binario
Object.keys(files).forEach(key => {
  console.log(`Procesando archivo: ${key}`);
  const fileData = files[key];

  if (fileData) {
    // No intentar acceder a fileData.data cuando es filesystem-v2
    // Solo registrar la metadata
    const imageInfo = {
      fileName: fileData.fileName || `imagen_${imageCount + 1}.jpg`,
      mimeType: fileData.mimeType || 'image/jpeg',
      originalName: fileData.fileName,
      index: imageCount,
      source: 'binary',
      binaryKey: key, // Guardar la clave para referencia
      // NO incluir data aqu√≠ - se manejar√° a trav√©s del binary
    };

    // Verificar si es filesystem-v2
    if (fileData.data === 'filesystem-v2') {
      imageInfo.isFilesystemRef = true;
      console.log(`‚úì Imagen ${key} est√° en filesystem-v2`);
    }

    allImages.push(imageInfo);
    imageCount++;
  }
});

console.log(`Total im√°genes procesadas: ${allImages.length}`);

// Identificar la infograf√≠a (la imagen m√°s grande o con nombre espec√≠fico)
let infografiaImage = allImages.find(img => 
  img.fileName.toLowerCase().includes('email') || 
  img.fileName.toLowerCase().includes('infografia')
);

if (!infografiaImage && allImages.length > 0) {
  // Si no hay una imagen espec√≠fica, usar la primera
  infografiaImage = allImages[0];
  console.log('Usando primera imagen como infograf√≠a');
}

// Separar elementos
const elementImages = allImages.filter(img => img !== infografiaImage);

// Generar ID √∫nico del proyecto
const projectId = `${projectName.replace(/[^a-zA-Z0-9]/g, '_')}_${Date.now()}`;
const timestamp = new Date().toISOString();

console.log('=== RESUMEN FINAL ===');
console.log(`Proyecto: ${projectName} (ID: ${projectId})`);
console.log(`Total im√°genes: ${allImages.length}`);
console.log(`Infograf√≠a: ${infografiaImage?.fileName || 'ninguna'}`);
console.log(`Elementos adicionales: ${elementImages.length}`);

// IMPORTANTE: Retornar en el formato correcto de n8n
return [{
  json: {
    project_id: projectId,
    project_name: projectName,
    timestamp: timestamp,
    infografia_metadata: infografiaImage,
    element_metadata: elementImages,
    total_images: allImages.length,
    all_images_metadata: allImages,
    ready_for_processing: true
  },
  // Pasar los binarios sin modificaci√≥n
  binary: files
}];

// Preparar estructura de carpetas organizada por proyecto - VERSI√ìN MEJORADA
const projectData = $input.first().json;
const siteId = projectData.id;
const accessToken = projectData.access_token;
const projectId = projectData.project_id;

// NUEVA ESTRUCTURA: Carpeta individual por ejecuci√≥n
