const outputFolder = projectFolder; // HTML se guarda en la ra√≠z del proyecto

console.log('üìÅ NUEVA ESTRUCTURA DE CARPETAS:');
console.log(`   Base: ${baseFolder}`);
console.log(`   Proyecto: ${projectFolder}`);
console.log(`   Infograf√≠a: ${infografiaFolder}`);
console.log(`   Elementos: ${elementosFolder}`);
console.log(`   Salida HTML: ${outputFolder}`);

return {
  json: {
    ...projectData,
    SITE_ID: siteId,
    access_token: accessToken,
