        description: 'Carpeta base para todos los proyectos'
      },
      {
        path: projectFolder,
        name: projectId,
        parent: baseFolder,
        description: 'Carpeta √∫nica del proyecto actual'
      },
      {
        path: infografiaFolder,
        name: 'infografia',
        parent: projectFolder,
        description: 'Solo para la imagen principal (m√°s pesada)'
      },
      {
        path: elementosFolder,
        name: 'elementos',
        parent: projectFolder,
        description: 'Para im√°genes adicionales/elementos'
      }
    ]
  }
};

// NODO: Preparar Prompt Claude - VERSI√ìN PRODUCCI√ìN
const allInputs = $input.all();

// Obtener datos del proyecto
let projectData = {};
let elementosData = [];
let infografiaBinary = null;

// 1. Datos del proyecto
try {
  const formDataNode = $('Procesar FormData1').first().json;
  if (formDataNode) {
    projectData.project_id = formDataNode.project_id;
    projectData.project_name = formDataNode.project_name;
  }
} catch (e) {
  console.log('Error obteniendo proyecto:', e.message);
}

// 2. Obtener imagen infograf√≠a desde Preparar Infograf√≠a
try {
  const prepararInfografia = $('Preparar Infograf√≠a').first();
